    private void pay_searchActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_pay_searchActionPerformed
        
        Function f = new Function();
        res = f.find(pay_empid.getText());
        try{
            if(res.next()){
                pay_name.setText(res.getString("name"));
                pay_lastname.setText(res.getString("lastname"));
                pay_dept.setText(res.getString("department"));
                pay_job.setText(res.getString("job"));
                pay_salaryhour.setText(res.getString("perhour"));
            }
            else{
                JOptionPane.showMessageDialog(null, "Employee ID do not exist!");
            }
        }catch(Exception e){
            JOptionPane.showMessageDialog(null, e);
        }
    }//GEN-LAST:event_pay_searchActionPerformed

