    private void pay_computeActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_pay_computeActionPerformed
        fetch();
        pay_hours.setText(Integer.toString(getsum()));
        int p = (getsum());
        Salary s = new Salary();
        res = s.find(pay_empid.getText());
        double sss = 0.08;
        double ph = 0.05;
        double pagibig = 0.02;
        double tax = 0.12;
        try{
            if(res.next()){
               int r =Integer.parseInt(res.getString("perhour"));
            int min = r * p;
            pay_salary.setText(Integer.toString(min));
            double tsss = sss * min;
            sss = Math.round(sss*100.0)/100.0;
            pay_sss.setText(Double.toString(tsss));
            double tph = ph * min; 
            tph = Math.round(tph*100.0)/100.0;
            pay_phhealth.setText(Double.toString(tph));
            double tpgb = pagibig * min; 
            tpgb = Math.round(tpgb*100.0)/100.0;
            pay_pagibig.setText(Double.toString(tpgb));
            double ttax = tax * min;
            ttax = Math.round(ttax*100.0)/100.0;
            pay_tax.setText(Double.toString(ttax));
            double less = ttax + tpgb + tph + tsss;
            double ttsalary = min - less;
            ttsalary = Math.round(ttsalary*100.0)/100.0;
            pay_totalsalary.setText(Double.toString(ttsalary));
            }
            
        }catch(Exception e){
            JOptionPane.showMessageDialog(null,"Incorrect Employee ID");
            
        }
          
    }//GEN-LAST:event_pay_computeActionPerformed
    public void fetch(){
        try{
            cn=DriverManager.getConnection("jdbc:mysql://localhost/payroll","root","");
            String q = "Select month as 'Month', day as 'Day', totalhours as 'Total Hours' from time where employeeid ="+ pay_empid.getText() +" order by day asc";
            
            ps = cn.prepareStatement(q);
            res=ps.executeQuery();
            
            summary.setModel(DbUtils.resultSetToTableModel(res));
        } 
        catch(Exception e){
            JOptionPane.showMessageDialog(null, "Incorrect Employee ID");
        }
    }
    public class Function{
      
        public ResultSet find(String s){
            try{
                cn=DriverManager.getConnection("jdbc:mysql://localhost/payroll","root","");
                ps = cn.prepareStatement("Select * from employee where employeeid = ?");
                ps.setString(1, s);
                res = ps.executeQuery();
                }      
                catch(Exception e){
                JOptionPane.showMessageDialog(null, e);
            }
            return res;
        }
    }
     public class Salary{
      
        public ResultSet find(String s){
            try{
                cn=DriverManager.getConnection("jdbc:mysql://localhost/payroll","root","");
                ps = cn.prepareStatement("Select perhour from employee where employeeid = ?");
                ps.setString(1, s);
                res = ps.executeQuery();
                }      
                catch(Exception e){
                JOptionPane.showMessageDialog(null, e);
            }
            return res;
        }
    }
    public int getsum(){
        int rowsCount = summary.getRowCount();
        int sum = 0;
        for(int i = 0; i < rowsCount; i++){
            sum = sum + Integer.parseInt(summary.getValueAt(i, 2).toString());
            
        }
        return sum;
    }
    
